CREATE PROC [dbo].[GET_STUDENT_PROFILE]
@studentId	INT
AS

BEGIN

	SET NOCOUNT ON;

		INSERT INTO [dbo].[Student_Table]
		(
			[firstName],
			[lastName],
			[email],
			[userName],
			[password],
			IsActive,
			IsAllowEdit
		)
		VALUES
		(
			@firstName,
			@lastName,
			@email,
			@userName,
			@password,
			@isActive,
			0

		)

END

